$(function(){$(window).scroll(function(){var o=$(".MasterHead");$(window).scrollTop()>680?$(o).css("background-color","#111"):$(o).css("background-color","rgba(0,0,0,0.35)")}),$(document).ready(function(){$(window).scroll(function(){$(".js-hideme").each(function(o){var n=$(this).offset().top+$(this).outerHeight();$(window).scrollTop()+$(window).height()>n&&TweenMax.to(this,1,{opacity:1,y:0})})})})});